export default function isFunction(value: unknown): value is (...args: unknown[]) => unknown {
    return typeof value === 'function' && 'call' in value && 'apply' in value;
}
import { isFunction } from '../object';

export function mergeProps(...props: any[]): object | undefined {
    return props?.reduce((merged, ps = {}) => {
        for (const key in ps) {
            const value = ps[key];

            if (key === 'style') {
                merged['style'] = { ...merged['style'], ...ps['style'] };
            } else if (key === 'class') {
                merged['class'] = [merged['class'], ps['class']].join(' ').trim() || undefined;
            } else if (key === 'className') {
                // For React
                merged['className'] = [merged['className'], ps['className']].join(' ').trim() || undefined;
            } else if (isFunction(value)) {
                const fn = merged[key];

                merged[key] = fn
                    ? (...args: any[]) => {
                          fn(...args);
                          value(...args);
                      }
                    : value;
            } else {
                merged[key] = value;
            }
        }

        return merged;
    }, {});
}
